const crypto = require('crypto');
const headers = $json.headers || {};
const secret = process.env.N8N_ISTANI_SHARED_SECRET || '';
const sig = headers['x-istani-signature'] || headers['X-Istani-Signature'] || '';
const body = JSON.stringify($json.body || {});
const calc = secret ? crypto.createHmac('sha256', secret).update(body).digest('hex') : '';

if (secret && sig !== calc) {
  return [{ ok: false, reason: 'bad-signature' }];
}

const data = $json.body || {};
console.log(`üèÉ iOS Health Data Received: ${data.type || 'unknown'}`);

return [{
  ok: true,
  dataType: data.type,
  payload: data,
  timestamp: new Date().toISOString()
}];

const data = $json.payload || {};

console.log('üí™ Processing Apple Fitness workout data');
console.log(`  Type: ${data.workoutType || 'N/A'}`);
console.log(`  Duration: ${data.duration || 0} minutes`);
console.log(`  Calories: ${data.calories || 0} kcal`);
console.log(`  Heart Rate: ${data.avgHeartRate || 0} bpm`);

const workout = {
  source: 'apple_health',
  type: data.workoutType || 'unknown',
  duration: parseFloat(data.duration) || 0,
  calories: parseFloat(data.calories) || 0,
  distance: parseFloat(data.distance) || 0,
  avgHeartRate: parseInt(data.avgHeartRate) || 0,
  maxHeartRate: parseInt(data.maxHeartRate) || 0,
  startTime: data.startTime || new Date().toISOString(),
  endTime: data.endTime || new Date().toISOString(),
  user: data.user || 'anonymous'
};

return [{
  status: 'workout_synced',
  workout: workout,
  message: `Synced ${workout.type} workout: ${workout.duration}min, ${workout.calories}cal`
}];

const data = $json.payload || {};

console.log('üëü Processing step count data');
console.log(`  Steps: ${data.count || 0}`);
console.log(`  Date: ${data.date || 'N/A'}`);

const steps = {
  source: 'apple_health',
  count: parseInt(data.count) || 0,
  date: data.date || new Date().toISOString().split('T')[0],
  floors: parseInt(data.floors) || 0,
  distance: parseFloat(data.distance) || 0,
  user: data.user || 'anonymous'
};

return [{
  status: 'steps_synced',
  steps: steps,
  message: `Synced ${steps.count} steps for ${steps.date}`
}];

const data = $json.payload || {};

console.log('‚ù§Ô∏è Processing heart rate data');
console.log(`  Heart Rate: ${data.bpm || 0} bpm`);
console.log(`  Timestamp: ${data.timestamp || 'N/A'}`);

const heartRate = {
  source: 'apple_health',
  bpm: parseInt(data.bpm) || 0,
  context: data.context || 'resting',
  timestamp: data.timestamp || new Date().toISOString(),
  user: data.user || 'anonymous'
};

return [{
  status: 'heart_rate_synced',
  heartRate: heartRate,
  message: `Synced heart rate: ${heartRate.bpm} bpm (${heartRate.context})`
}];

const data = $json.payload || {};

console.log('üò¥ Processing sleep data');
console.log(`  Duration: ${data.duration || 0} hours`);
console.log(`  Quality: ${data.quality || 'N/A'}`);

const sleep = {
  source: 'apple_health',
  duration: parseFloat(data.duration) || 0,
  quality: data.quality || 'unknown',
  deepSleep: parseFloat(data.deepSleep) || 0,
  remSleep: parseFloat(data.remSleep) || 0,
  startTime: data.startTime || new Date().toISOString(),
  endTime: data.endTime || new Date().toISOString(),
  user: data.user || 'anonymous'
};

return [{
  status: 'sleep_synced',
  sleep: sleep,
  message: `Synced sleep: ${sleep.duration}h (${sleep.quality})`
}];
