const crypto = require('crypto');
const headers = $json.headers || {};
const secret = process.env.N8N_ISTANI_SHARED_SECRET || '';
const sig = headers['x-istani-signature'] || headers['X-Istani-Signature'] || '';
const body = JSON.stringify($json.body || {});
const calc = secret ? crypto.createHmac('sha256', secret).update(body).digest('hex') : '';

if (secret && sig !== calc) {
  return [{ ok: false, reason: 'bad-signature' }];
}

const data = $json.body || {};
console.log(`ðŸ“² Notification Request: ${data.title || 'N/A'}`);

return [{
  ok: true,
  notification: {
    title: data.title || 'ISTANI Fitness',
    body: data.body || '',
    badge: data.badge || 0,
    sound: data.sound || 'default',
    category: data.category || 'fitness',
    data: data.data || {},
    deviceToken: data.deviceToken || '',
    user: data.user || 'anonymous'
  },
  timestamp: new Date().toISOString()
}];

const notif = $json.notification || {};

console.log('ðŸ“² Sending iOS notification via APNs');
console.log(`  Title: ${notif.title}`);
console.log(`  Body: ${notif.body}`);
console.log(`  Device: ${notif.deviceToken ? 'provided' : 'broadcast'}`);

