const crypto = require('crypto');
const headers = $json.headers || {};
const secret = process.env.N8N_ISTANI_SHARED_SECRET || '';
const sig = headers['x-istani-signature'] || headers['X-Istani-Signature'] || '';
const body = JSON.stringify($json.body || {});
const calc = secret ? crypto.createHmac('sha256', secret).update(body).digest('hex') : '';

if (secret && sig !== calc) {
  return [{ ok: false, reason: 'bad-signature' }];
}

const data = $json.body || {};
console.log(`ðŸ“± iOS Shortcuts Event: ${data.action || 'unknown'}`);

return [{
  ok: true,
  event: data.action,
  payload: data,
  timestamp: new Date().toISOString()
}];

const data = $json.payload || {};

console.log('ðŸ’ª Logging workout from iOS Shortcuts:');
console.log(`  Exercise: ${data.exercise || 'N/A'}`);
console.log(`  Sets: ${data.sets || 0}`);
console.log(`  Reps: ${data.reps || 0}`);
console.log(`  Weight: ${data.weight || 0}kg`);

// Store workout in n8n database or forward to WordPress API
const workout = {
  source: 'ios_shortcuts',
  exercise: data.exercise,
  sets: parseInt(data.sets) || 0,
  reps: parseInt(data.reps) || 0,
  weight: parseFloat(data.weight) || 0,
  notes: data.notes || '',
  timestamp: data.timestamp || new Date().toISOString(),
  user: data.user || 'anonymous'
};

return [{
  status: 'workout_logged',
  workout: workout,
  message: `Logged ${workout.exercise}: ${workout.sets}x${workout.reps} @ ${workout.weight}kg`
}];

const data = $json.payload || {};

console.log('ðŸ“Š Processing generic iOS action');

return [{
  status: 'processed',
  action: $json.event,
  data: data,
  message: 'iOS shortcut action processed successfully'
}];
