const crypto = require('crypto');
const headers = $json.headers || {};
const secret = process.env.N8N_ISTANI_SHARED_SECRET || '';
const sig = headers['x-istani-signature'] || headers['X-Istani-Signature'] || '';
const body = JSON.stringify($json.body || {});
const calc = secret ? crypto.createHmac('sha256', secret).update(body).digest('hex') : '';
if (secret && sig !== calc) {
  return [{ ok: false, reason: 'bad-signature' }];
}
return [{ ok: true, contact: $json.body || {} }];
